package Operators;

public class Assignment {
	
	static int num = 320;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        // Assignment operator:
		System.out.println(num+=80);
		System.out.println(num-=100);
		System.out.println(num*=100);
		System.out.println(num/=10);
		System.out.println(num%=3);
	}

}
